<?php

class Item
{
    public string $name;
    public float $price;
    public string $url;
    public function __construct(string $name, float $price, string $url){
        $this->name = $name;
        $this->price = $price;
        $this->url= $url;
    }
    public function display():void {
        echo "Nom: ($this->name)<br>";
        echo "Prix: ($this->price) euro <br>";
        echo "URL: <a href=\"($this->url)\">($this->url)</a><br>";
    }
}
$item= new Item("Canard", 50.99,"data:image/jpeg;base64,");
$item->display();



?>